provider "azurerm" {
  features {}
}
resource "random_pet" "db_name" {
  length    = "1"
  prefix    = var.prefix
  separator = "-"
}
resource "azurerm_resource_group" "example" {
  name     = "${var.prefix}-rg"
  location = var.location
}
variable "location" {
  description = "Location where resources will be created"
  type        = string
}
variable "prefix" {
  description = "Prefix for resource names"
  type        = string
}
resource "azurerm_postgresql_server" "example" {
  name                = random_pet.db_name.id
  location            = azurerm_resource_group.example.location
  resource_group_name = azurerm_resource_group.example.name
  administrator_login = var.pguser
  administrator_password = var.pgpass
  version             = "9.6"
  capacity            = 2
  storage_mb          = 51200

  tags = {
    environment = "staging"
    database    = azurerm_postgresql_server.example.name
  }
}
variable "pguser" {
  description = "The PostgreSQL administrator username"
  type        = string
}
variable "pgpass" {
  description = "The PostgreSQL administrator password"
  type        = string
  sensitive   = true
}
resource "azurerm_postgresql_database" "example" {
  name                = random_pet.db_name.id
  resource_group_name = azurerm_resource_group.example.name
  server_id           = azurerm_postgresql_server.example.id

  tags = {
    environment = "staging"
    database    = azurerm_postgresql_database.example.name
  }
}
