# main.tf
provider "azurerm" {
  features {}
}
variable "resource_group_name" {
  description = "The name of the resource group in which to create the PostgreSQL server."
  type        = string
}
variable "location" {
  description = "The location where the resource group should be created."
  type        = string
}
variable "server_name" {
  description = "The name of the Azure PostgreSQL Server."
  type        = string
}
variable "sku_name" {
  description = "The name of the SKU to use for this server."
  default     = "GP_Gen5_2"
  type        = string
}
variable "admin_username" {
  description = "TheAdministrator username for the PostgreSQL Server."
  default     = "dbadmin"
  type        = string
}
variable "admin_password" {
  description = "The password associated with the admin user of the database."
  default     = ""
  type        = string
}
variable "database_name" {
  description = "The name of the database to be created on the server."
  default     = "myDatabase"
  type        = string
}
resource "azurerm_resource_group" "example" {
  name     = var.resource_group_name
  location = var.location
}
resource "azurerm_postgresql_server" "example" {
  name                = var.server_name
  resource_group_name = azurerm_resource_group.example.name
  location            = azurerm_resource_group.example.location
  sku_name            = var.sku_name
  storage_mb          = 5120
  version             = "9.6"
  administrator_login = var.admin_username
  administrator_password = var.admin_password

  tags = {
    environment = "production"
  }
}
resource "azurerm_postgresql_database" "example" {
  name                = var.database_name
  resource_group_name = azurerm_resource_group.example.name
  server_id           = azurerm_postgresql_server.example.id
}
