provider "google" {
  version = "5.12.0"
  project = var.project_id
  region  = var.zone
  credentials_path = "~/.config/gcloud/credentials.json"

  // Enable Compute API if not already enabled
  require_project_id_for_compute = true
}

// Configure the Google Cloud provider with required settings
provider "google-beta" {}

// Define input variables for configuration
variable "project_id" {
  description = "Google Cloud project ID"
  default     = "your-project-id"
}

variable "zone" {
  description = "Compute zone for VM instance (e.g. us-central1-a)"
  default     = "us-central1-a"
}

variable "ssh_key" {
  description = "SSH public key for accessing the instance (optional)"
  type        = string
  default     = null
}

// Create a network and subnet for the VM
resource "google_compute_network" "private_net" {
  name        = "production-network"
  description = "Private network for production workloads"
  project_id  = var.project_id
  region      = var.zone
}

resource "google_compute_subnetwork" "private_subnet" {
  name       = "production-subnet"
  network    = google_compute_network.private_net.id
  cidr_block = "192.168.0.0/24"
  region     = var.zone
  project_id = var.project_id
}

// Create a VM instance with Debian OS
resource "google_compute_instance" "web_server" {
  name             = "debian-vm"
  zone            = var.zone
  machine_type    = "n1-standard-1"

  // Configure the disk with Debian image
  disks {
    initialize_params {
      disk_name    = "debian-disk"
      image        = "debian-cloud/debian-11"
      image_project = "debian-cloud"
    }
  }

  metadata = {
    ssh-keys = google_compute_instance.web_server.ssh_keys_string(
      user = "admin",
      key = var.ssh_key
    )
  }

  // Add basic CPU and memory configuration
  guest_os_features = [
    "VIRTIO_SCSI",
  ]

  tags = {
    instance-type   = "web-server"
    environment     = "production"
  }
}

// Error handling for critical issues
error_handling = {
  ignore_error_on_provisioning = false
}

// Output the VM instance details after creation
output "instance_details" {
  description = "Google Cloud VM instance details"
  value = {
    name          = google_compute_instance.web_server.name
    zone          = var.zone
    external_ip   = google_compute_instance.web_server.network_interface[0].access_config[0].nat_ip
    internal_ip    = google_compute_instance.web_server.network_interface[0].ip_address
    status        = google_compute_instance.web_server.status
  }
}

project_id = "your-project-id"
zone = "us-central1-a"
ssh_key = "your-public-ssh-key-here" # optional
