# Configure the Google Cloud provider with the required version
provider "google" {
  version = "5.10.0"

  # Set up GCP credentials from environment variables
  credentials = google_credentials GOOGLE_CREDENTIALS
}

variable "project_id" {
  description = "ID of the Google Cloud project where resources will be created."
  default     = "your-project-id"
}

variable "region" {
  description = "Google Cloud region for the VM instance."
  default     = "us-central1"
}

variable "zone" {
  description = "Compute zone within the selected region."
  default     = "us-central1-a"
}

variable "instance_name" {
  description = "Name of the VM instance."
  default     = "debian-vm"
}

variable "machine_type" {
  description = "Machine type for the VM instance (e.g., g1-small, n1-standard-2)."
  default     = "g1-small"
}

variable "boot_disk_size" {
  description = "Size of the boot disk in GB."
  default     = "10"
}

# Enable Google provider
include "provider.google"

# Include local files for variables and configurations
include "variables"

include " terraform"

# Main Terraform configuration
resource "google_compute_instance" "${var.instance_name}" {
  # Ensure proper resource dependencies
  depends_on = [
    google_compute_image.debian-image,
  ]

  name             = var.instance_name
  zone            = var.zone
  machine_type     = var.machine_type

  # Use the Debian image from the Compute Engine family
  boot_disk {
    initialize_params {
      disk_size_gb = var.boot_disk_size
      image        = "debian-cloud/debian-12"
      image_family = "debian-12"
    }
  }

  # Add metadata for SSH access (replace with actual key)
  metadata = {
    ssh-keys = "ssh-rsa AAAAB3NzaC1yc2E... user@host" 
  }

  tags = {
    TerraformManaged = "true",
    purpose          = "production-server"
  }
}

# Import the Debian image for reference
data "google_compute_image" "debian-image" {
  name     = "debian-cloud/debian-12"
  project  = var.project_id
}

project_id = "your-project-id"
region     = "us-central1"
zone       = "us-central1-a"
instance_name = "debian-vm"
machine_type = "g1-small"
boot_disk_size = "10"
